import React from  'react';

class form extends React.Component{
   constructor(props){
       super(props);
       this.state = {
           form: 'Form',
           dados: 'Dados',
           nome: '',
           idade: '',
           estado: ''
       }
   }
   mudarNome = (ev) => {
        this.setState({
            nome: ev.target.value,
            idade: ev.target.value,
            estado: ev.target.value
        })
    }
