    render() {
        return (
            <div>
                <h1 className='title'>
                    {this.state.form}
                </h1>
            <span>
                <input onChange={this.mudarNome} type='text'/>
                <input onChange={this.mudarNome} type='text'/>
                <input onChange={this.mudarNome} type='text'/>
            </span>
            <h1>
                {this.state.dados}
            </h1>
            <p
            color={this.props.color}
            >{this.props.nome}</p>
            <p>{this.props.idade}</p>
            <p>{this.props.estado}</p>
            </div>
        )
    }
}


export default form;
