       }
   }

    apagar = () => {
        this.setState({
            lampada: 'apagada',
        })
    }
    acender = () => {
        this.setState({
            lampada: 'acesa',
        })
    }

    render() {
        return (
            <div>
                <h1 className='title'>
                    {this.state.lampada}
                </h1>
            <span>
                <input type='text'></input>
                <input type='text'></input>
                <input type='text'></input>
            </span>
            </div>
        )
    }
}


